import { HttpClient, HttpHeaders, HttpParams, HttpRequest, HttpResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, map } from 'rxjs';

import { Result, resultList, Details, DetailsArray, sourceList, Source } from './movie-list/interfaces';



@Injectable({
  providedIn: 'root'
})
export class MoviesService {

  constructor(private http:HttpClient) { }

// Get search results 
  getMovieTitle(search_query: string, ):Observable<Details[]>{

    return this.http.get<resultList>(`/api/search/${search_query}`).pipe(map);

  }
// Get streaming sources
  getMovieInfo(
    title_id: number, 
    ): Observable<Source>{

      return this.http.get<Source>(`/api/details/${title_id}`);

    }

// Test to make sure server connection is active. 
    getAPIStatus(){
      return this.http.get("/api/status", {responseType: 'text'});
    }

}
