import { Component} from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})

export class HeaderComponent {

  constructor(
    private router: Router, 
    private route: ActivatedRoute
    ){}

  SearchText: string = '';

  search(SearchText: string){

    if(this.SearchText != ''){

      this.router.navigate(['search/'], {
        relativeTo: this.route,
        queryParams:{
          query: SearchText
        },
        queryParamsHandling: 'merge',
        skipLocationChange: false
      })




    }else{
      console.log("Empty search term");
    }
  }
}
<link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@48,400,0,0" />
<div class="theme-wrapper theme-light">
<div class=" navbar ">
    <a class="logo" routerLink='/'>
        <h1>Watchable</h1><span class="material-symbols-outlined">
            play_circle
            </span>
    </a>

    <div class="button-row">
        <span type="button" (click)="search(SearchText)"   routerLinkActive="active"  class="material-symbols-outlined">
            search
            </span>
        <input type="text" id="SearchText" name="SearchText" [(ngModel)] = "SearchText" >
    </div>
</div>
</div>
